import React from "react";
import Info from "./components/info";
import Form from "./components/form";
import Weather from "./components/weather";

const apiKey = "55a346ef64175467a93be9bfb320e5fe";

class App extends React.Component {
  state = {

  }


  gettingWeather = async (e) => {
    e.preventDefault();
    const city = e.target.element.city.value;
    const apiUrl = await fetch (`api.openweathermap.org/data/2.5/weather?q=${city},ua&appid=${apiKey}&units=metric`);
    const data = await apiUrl.json();
  }
  render () {
    return (
      <div>
      <div><Info/></div>
      <div><Form gettingWeather = {this.gettingWeather}/></div>
      <div><Weather/></div>
      </div>
    );
  }
}

export default App;
